World.ShaderGroup:
param float r 0;
param float g 1;
param float b 1;
shader constant constTex;

param float scale_s 20;
param float scale_t 20;
param color Ca 0 0 0;
param color Cb 0 0 0;
shader checkerboard checkTex;

shader matte layer1;
connect constTex.Cout[1] checkTex.Ca
connect constTex.Cout[2] checkTex.Cb[2]
connect checkTex.Cout[0] layer1.Cs[2];
connect checkTex.Cout[1] layer1.Cs[1];
connect checkTex.Cout[2] layer1.Cs[0];

param float r 1;
param float g 0;
param float b 0;
shader constant tex;
shader matte layer1;
connect tex.Cout[0] layer1.Cs[0];

param float r 0;
param float g 1;
param float b 0;
shader constant tex;
shader matte layer1;
connect tex.Cout[1] layer1.Cs[1];

param float r 0;
param float g 0;
param float b 1;
shader constant tex;
shader matte layer1;
connect tex.Cout[2] layer1.Cs[2];
